#!/bin/bash
curl -i -s -k -X $'GET' \
    -H $"$1" -H $'Connection: close' -H $'sec-ch-ua: \";Not A Brand\";v=\"99\", \"Chromium\";v=\"88\"' -H $'sec-ch-ua-mobile: ?0' -H $'User-Agent: Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/88.0.4324.150 Safari/537.36' -H $'Accept: image/avif,image/webp,image/apng,image/svg+xml,image/*,*/*;q=0.8' -H $'Sec-Fetch-Site: same-origin' -H $'Sec-Fetch-Mode: no-cors' -H $'Sec-Fetch-Dest: image' -H $'Referer: '"$1" -H $'Accept-Encoding: gzip, deflate' -H $'Accept-Language: en-US,en;q=0.9' \
    -b $'XSRF-%3D%3D; laravel_%3D' \
    $"$1"'/uno-logo-color.png' >TransportSec.txt && cat TransportSec.txt
